t1=("A","B","C")
print(type(t1))
print(t1)

t2=("A")            #Single character so class is str
print(type(t2))
print(t2)

t3=("A",)           #For single object in tuple we need to give comma after the item
print(type(t3))
print(t3)

#Tuple can be of any data type
t4=(1,3,5,7,8)
t5=("a","b","c")
t6=(True,False)
print(t4)
print(t5)
print(t6)